import React, { useState } from "react";
import { Box, Typography, Drawer, List, ListItemButton, ListItemText, Button, Divider, Alert, Container, TextField } from "@mui/material";

const PatientDashboard = () => {
  // Logged-in user
  const user = JSON.parse(localStorage.getItem("user")) || { name: "Patient", role: "patient" };

  // Mock data
  const appointments = [
    { id: 1, doctor: "Dr. Alice Uwase", department: "Cardiology", datetime: "2025-11-03 09:00", status: "Scheduled" },
    { id: 2, doctor: "Dr. John Niyonsenga", department: "General Medicine", datetime: "2025-11-05 11:00", status: "Scheduled" },
  ];
  const records = [
    { id: 1, type: "Blood Test", doctor: "Dr. Alice Uwase", date: "2025-10-20", notes: "Normal" },
    { id: 2, type: "X-Ray", doctor: "Dr. John Niyonsenga", date: "2025-10-15", notes: "Minor fracture" },
  ];

  // Booking form state
  const [doctorId, setDoctorId] = useState("");
  const [datetime, setDatetime] = useState("");
  const [notes, setNotes] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState("");

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  const handleBookAppointment = async (e) => {
    e.preventDefault();
    setMessage(""); setStatus("");

    if (!doctorId || !datetime) {
      setMessage("Doctor and Date/Time are required.");
      setStatus("error");
      return;
    }

    // Convert to 'YYYY-MM-DD HH:MM:SS'
    let formattedDatetime = new Date(datetime).toISOString().slice(0, 19).replace("T", " ");

    // Call API
    try {
      const res = await fetch("http://localhost/healthcare_backend/api/patient/bookAppointment.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ patient_id: user.user_id, doctor_id: doctorId, start_datetime: formattedDatetime, notes }),
      });
      const data = await res.json();
      setMessage(data.message);
      setStatus(data.success ? "success" : "error");
      if (data.success) {
        setDoctorId(""); setDatetime(""); setNotes("");
      }
    } catch (err) {
      console.error(err);
      setMessage("Server error.");
      setStatus("error");
    }
  };

  return (
    <Box sx={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <Drawer
        variant="permanent"
        sx={{
          width: 240,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: 240, boxSizing: "border-box" },
        }}
      >
        <Box sx={{ p: 2, bgcolor: "#1976d2", color: "#fff", textAlign: "center" }}>
          <Typography variant="h6">{user.name}</Typography>
          <Typography variant="body2">{user.role.toUpperCase()}</Typography>
        </Box>
        <Divider />
        <List>
          <ListItemButton>
            <ListItemText primary="Dashboard" />
          </ListItemButton>
          <ListItemButton>
            <ListItemText primary="Appointments" />
          </ListItemButton>
          <ListItemButton>
            <ListItemText primary="Medical Records" />
          </ListItemButton>
          <ListItemButton>
            <ListItemText primary="Book Appointment" />
          </ListItemButton>
          <ListItemButton onClick={handleLogout}>
            <ListItemText primary="Logout" />
          </ListItemButton>
        </List>
      </Drawer>

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, p: 4, bgcolor: "#f4f6f8", overflowY: "auto" }}>
        <Typography variant="h4" gutterBottom>
          Welcome, {user.name}
        </Typography>

        {/* Upcoming Appointments */}
        <Box sx={{ mb: 4, p: 2, bgcolor: "#fff", borderRadius: 2, boxShadow: 1 }}>
          <Typography variant="h6" gutterBottom>
            Upcoming Appointments
          </Typography>
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>#</th>
                <th>Doctor</th>
                <th>Department</th>
                <th>Date & Time</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((appt) => (
                <tr key={appt.id}>
                  <td>{appt.id}</td>
                  <td>{appt.doctor}</td>
                  <td>{appt.department}</td>
                  <td>{appt.datetime}</td>
                  <td>{appt.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </Box>

        {/* Recent Medical Records */}
        <Box sx={{ mb: 4, p: 2, bgcolor: "#fff", borderRadius: 2, boxShadow: 1 }}>
          <Typography variant="h6" gutterBottom>
            Recent Medical Records
          </Typography>
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>#</th>
                <th>Type</th>
                <th>Doctor</th>
                <th>Date</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {records.map((rec) => (
                <tr key={rec.id}>
                  <td>{rec.id}</td>
                  <td>{rec.type}</td>
                  <td>{rec.doctor}</td>
                  <td>{rec.date}</td>
                  <td>{rec.notes}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </Box>

        {/* Book Appointment Form */}
        <Box sx={{ p: 2, bgcolor: "#fff", borderRadius: 2, boxShadow: 1 }}>
          <Typography variant="h6" gutterBottom>
            Book New Appointment
          </Typography>

          {message && <Alert severity={status} sx={{ mb: 2 }}>{message}</Alert>}

          <form onSubmit={handleBookAppointment}>
            <TextField
              label="Doctor ID"
              type="number"
              fullWidth
              value={doctorId}
              onChange={(e) => setDoctorId(e.target.value)}
              margin="normal"
            />
            <TextField
              label="Appointment Date & Time"
              type="datetime-local"
              fullWidth
              value={datetime}
              onChange={(e) => setDatetime(e.target.value)}
              margin="normal"
            />
            <TextField
              label="Notes"
              multiline
              rows={3}
              fullWidth
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              margin="normal"
            />
            <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
              Book Appointment
            </Button>
          </form>
        </Box>
      </Box>
    </Box>
  );
};

export default PatientDashboard;
