import React, { useState } from "react";
import Header from "../../components/Header";
import PatientSidebar from "../../components/PatientSidebar";
import "./PatientDashboard.css";

const PatientDashboard = () => {
  const user = JSON.parse(localStorage.getItem("user")) || {
    name: "Patient",
    role: "patient",
  };

  const [activeMenu, setActiveMenu] = useState("dashboard");

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  // Mock Data
  const appointments = [
    { id: 1, doctor: "Dr. Alice Uwase", department: "Cardiology", datetime: "2025-11-03 09:00", status: "Scheduled" },
    { id: 2, doctor: "Dr. John Niyonsenga", department: "General Medicine", datetime: "2025-11-05 11:00", status: "Scheduled" },
  ];

  const records = [
    { id: 1, type: "Blood Test", doctor: "Dr. Alice Uwase", date: "2025-10-20", notes: "Normal" },
    { id: 2, type: "X-Ray", doctor: "Dr. John Niyonsenga", date: "2025-10-15", notes: "Minor fracture" },
  ];

  // Dynamic content
  const renderContent = () => {
    switch (activeMenu) {
      case "dashboard":
        return <h2>Welcome, {user.name}!</h2>;
      case "book":
        return <div>üìù Book Appointment Form here</div>;
      case "appointments":
        return (
          <div>
            <h3>My Appointments</h3>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Doctor</th>
                  <th>Department</th>
                  <th>Date & Time</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {appointments.map((appt) => (
                  <tr key={appt.id}>
                    <td>{appt.id}</td>
                    <td>{appt.doctor}</td>
                    <td>{appt.department}</td>
                    <td>{appt.datetime}</td>
                    <td>{appt.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      case "records":
        return (
          <div>
            <h3>Medical Records</h3>
            <table>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Type</th>
                  <th>Doctor</th>
                  <th>Date</th>
                  <th>Notes</th>
                </tr>
              </thead>
              <tbody>
                {records.map((rec) => (
                  <tr key={rec.id}>
                    <td>{rec.id}</td>
                    <td>{rec.type}</td>
                    <td>{rec.doctor}</td>
                    <td>{rec.date}</td>
                    <td>{rec.notes}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        );
      default:
        return <h2>Welcome, {user.name}!</h2>;
    }
  };

  return (
    <div className="dashboard-container">
      <PatientSidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div className="dashboard-main">
        <Header user={user} onLogout={handleLogout} />
        <main className="dashboard-content">{renderContent()}</main>
      </div>
    </div>
  );
};

export default PatientDashboard;
