// src/api/patient.js
import axios from "axios";

export const bookAppointment = async ({ patient_id, doctor_id, start_datetime }) => {
  try {
    const response = await axios.post(
      "http://localhost/healthcare_systehealthcare_backend/api/patient/bookAppointment.php",
      { patient_id, doctor_id, start_datetime }
    );
    return response.data;
  } catch (error) {
    console.error("Error booking appointment:", error);
    return { success: false, message: "Server error." };
  }
};
