import React, { useState } from "react";
import { Container, TextField, Button, Typography, Alert } from "@mui/material";
import { bookAppointment } from "../../api/patient";

const BookAppointmentForm = ({ patientId }) => {
  const [doctorId, setDoctorId] = useState("");
  const [datetime, setDatetime] = useState("");
  const [notes, setNotes] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(""); setStatus("");

    if (!doctorId || !datetime) {
      setMessage("Doctor and Date/Time are required.");
      setStatus("error");
      return;
    }

    // Convert to 'YYYY-MM-DD HH:MM:SS'
    let formattedDatetime = new Date(datetime).toISOString().slice(0, 19).replace("T", " ");

    const res = await bookAppointment({
      patient_id: patientId,
      doctor_id: doctorId,
      start_datetime: formattedDatetime,
      notes,
    });

    setMessage(res.message);
    setStatus(res.success ? "success" : "error");
  };

  return (
    <Container maxWidth="sm">
      {message && <Alert severity={status}>{message}</Alert>}
      <form onSubmit={handleSubmit}>
        <TextField
          label="Doctor ID"
          type="number"
          fullWidth
          value={doctorId}
          onChange={(e) => setDoctorId(e.target.value)}
          margin="normal"
        />
        <TextField
          label="Appointment Date & Time"
          type="datetime-local"
          fullWidth
          value={datetime}
          onChange={(e) => setDatetime(e.target.value)}
          margin="normal"
        />
        <TextField
          label="Notes"
          multiline
          rows={3}
          fullWidth
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          margin="normal"
        />
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Book Appointment
        </Button>
      </form>
    </Container>
  );
};

export default BookAppointmentForm;
