import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import './PatientDashboard.css';
import { getAppointments, getMedicalRecords, bookAppointment } from '../../api/patient';
import { TextField, Button, Select, MenuItem, FormControl, InputLabel } from '@mui/material';

const PatientDashboard = () => {
  const user = JSON.parse(localStorage.getItem('user')) || { name: 'Patient', user_id: 1, role: 'patient' };

  const [appointments, setAppointments] = useState([]);
  const [records, setRecords] = useState([]);
  const [doctors, setDoctors] = useState([
    { id: 1, name: 'Dr. Alice Uwase' },
    { id: 2, name: 'Dr. John Niyonsenga' }
  ]);
  const [selectedDoctor, setSelectedDoctor] = useState(doctors[0].id);
  const [selectedDateTime, setSelectedDateTime] = useState('');

  // Fetch appointments & records on mount
  useEffect(() => {
    fetchAppointments();
    fetchRecords();
  }, []);

  const fetchAppointments = async () => {
    const res = await getAppointments(user.user_id);
    if (res.success) setAppointments(res.data);
  };

  const fetchRecords = async () => {
    const res = await getMedicalRecords(user.user_id);
    if (res.success) setRecords(res.data);
  };

  const handleBookAppointment = async () => {
    if (!selectedDateTime) return alert("Please select date and time.");

    const res = await bookAppointment({
      patient_id: user.user_id,
      doctor_id: selectedDoctor,
      start_datetime: selectedDateTime
    });

    alert(res.message);
    if (res.success) fetchAppointments();
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/';
  };

  return (
    <div className="dashboard-container">
      <Header user={user} onLogout={handleLogout} />
      <Sidebar role={user.role} />

      <main className="dashboard-content">
        <h2>Welcome, {user.name}</h2>

        {/* Book Appointment Form */}
        <section className="card-section">
          <h3>Book New Appointment</h3>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel>Doctor</InputLabel>
            <Select value={selectedDoctor} onChange={(e) => setSelectedDoctor(e.target.value)}>
              {doctors.map((doc) => (
                <MenuItem key={doc.id} value={doc.id}>{doc.name}</MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            label="Date & Time"
            type="datetime-local"
            fullWidth
            sx={{ mb: 2 }}
            value={selectedDateTime}
            onChange={(e) => setSelectedDateTime(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={handleBookAppointment}>Book Appointment</Button>
        </section>

        {/* Upcoming Appointments */}
        <section className="card-section">
          <h3>Upcoming Appointments</h3>
          <table className="appointments-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Doctor</th>
                <th>Department</th>
                <th>Date & Time</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((appt) => (
                <tr key={appt.appointment_id}>
                  <td>{appt.appointment_id}</td>
                  <td>{appt.doctor}</td>
                  <td>{appt.department}</td>
                  <td>{appt.start_datetime}</td>
                  <td>{appt.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Medical Records */}
        <section className="card-section">
          <h3>Recent Medical Records</h3>
          <table className="records-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Type</th>
                <th>Doctor</th>
                <th>Date</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {records.map((rec) => (
                <tr key={rec.id}>
                  <td>{rec.id}</td>
                  <td>{rec.type}</td>
                  <td>{rec.doctor}</td>
                  <td>{rec.date}</td>
                  <td>{rec.notes}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </main>
    </div>
  );
};

export default PatientDashboard;
