import React, { useState } from "react";
import { Container, TextField, Button, Typography, Box, Alert } from "@mui/material";
import { loginUser } from "../api/auth";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [status, setStatus] = useState(""); // track success or error
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setStatus("");

    if (!email || !password) {
      setMessage("All fields are required");
      setStatus("error");
      return;
    }

    try {
      const res = await loginUser({ email, password });
      setMessage(res.message);
      setStatus(res.status);

      if (res.status === "success") {
        // Optionally save user info to localStorage/session
        localStorage.setItem("user", JSON.stringify(res));

        // Redirect based on role
        switch (res.role) {
          case "patient": navigate("/patient-dashboard"); break;
          case "doctor": navigate("/doctor-dashboard"); break;
          case "admin": navigate("/admin-dashboard"); break;
          default: navigate("/"); break;
        }
      }
    } catch (err) {
      setMessage("Server error. Please try again.");
      setStatus("error");
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 10, p: 4, boxShadow: 3, borderRadius: 2 }}>
        <Typography variant="h4" gutterBottom align="center">Login</Typography>
        {message && <Alert severity={status === "success" ? "success" : "error"} sx={{ mb: 2 }}>{message}</Alert>}
        <form onSubmit={handleSubmit}>
          <TextField
            label="Email"
            type="email"
            fullWidth
            required
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            required
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            Login
          </Button>
        </form>
        <Typography sx={{ mt: 2, textAlign: "center" }}>
          Don't have an account? <a href="/register">Register</a>
        </Typography>
      </Box>
    </Container>
  );
};

export default Login;
