import axios from "axios";

const BASE_URL = "http://localhost/healthcare_system/healthcare-backend/controllers";

// Register user
export const registerUser = async (formData) => {
  try {
    const response = await axios.post(`${BASE_URL}/register.php`, formData, {
      headers: {
        "Content-Type": "application/json",
      },
    });
    return response.data;
  } catch (error) {
    console.error("Registration error:", error);
    return { status: "error", message: "Server error. Please try again." };
  }
};

// Login user
export const loginUser = async (formData) => {
  try {
    const response = await axios.post(`${BASE_URL}/login.php`, formData, {
      headers: {
        "Content-Type": "application/json",
      },
    });
    return response.data;
  } catch (error) {
    console.error("Login error:", error);
    return { status: "error", message: "Server error. Please try again." };
  }
};
